#!/bin/bash

string="MEGCDSPVVSGKDNGCGLPQHQQWTELNSTHLPDKASSMEQSTGESHGPLDSLRAPFNERLAESAAPAGPPAEPASKEVSCNECAASFASLQTYMEHHCPGARPPPPLREESASDTSEEGEEESDVENLAGEIVYQPDGSAYIVESLSQLTQGGAAC-------"

# Calculate the total number of characters
total_chars=$(echo -n "$string" | wc -c)

# Declare an associative array to hold letter counts
declare -A letter_counts

# Loop over each character in the string
for (( i=0; i<${#string}; i++ )); do
  letter="${string:$i:1}"
  (( letter_counts["$letter"]++ ))
done

# Print the counts and proportions for each letter
for letter in "${!letter_counts[@]}"; do
  count=${letter_counts[$letter]}
  proportion=$(echo "scale=4; $count / $total_chars * 100" | bc -l)
  echo "The AA '$letter' occurs $count times in the string."
  echo "The proportion of AA '$letter' in the protein is $proportion."
done
